#include <vector>
#include <algorithm>
#include <tuple>
#include <iostream>
using namespace std;

class Solution {
public:
    const static int MODULO = 1e9 + 7;

    int threeSumMulti(vector<int>& A, int target) {
        uint64_t result = 0;
        std::sort(A.begin(), A.end());

        std::vector<int> numCount(101, 0);
        for (const auto &num: A) {
            numCount[num]++;
        }

        int prevIndex = -1;
        for (int i = 0; i < A.size(); i++) {
            if (prevIndex != -1 && A[prevIndex] == A[i]) {
                continue;
            }
            vector<tuple<int, int, int>> targetTuple = this->checkSum(A, i, target - A[i]);
            for (const auto &targetCombine: targetTuple) {
                int x, y, z;
                std::tie(x, y, z) = targetCombine;
                if (x == y && x == z) {
                    result += (numCount[x] * (numCount[x] - 1L) * (numCount[x] - 2L) / 6L);
                } else if (x == y || x == z || y == z) {
                    // make x couple, y single
                    if (x == y) {
                        y = z;
                    } else if (y == z) {
                        std::swap(x, y);
                    }
                    result += (numCount[x] * (numCount[x] - 1L) / 2L) * numCount[y];
                } else {
                    result += numCount[x] * numCount[y] * numCount[z];
                }
                result = result % MODULO;
            }

            prevIndex = i;
        }

        return result;
    }

    vector<tuple<int, int, int>> checkSum(vector<int> &A, int index, int target) {
        vector<tuple<int, int, int>> targetTuple;
        int p = index + 1, q = A.size() - 1;
        while (p < q) {
            if (p == index) {
                p++;
            } else if (q == index) {
                q--;
            } else {
                int sum = A[p] + A[q];
                if (sum > target) {
                    q--;
                } else if (sum < target) {
                    p++;
                } else {
                    targetTuple.push_back(make_tuple(A[index], A[p], A[q]));
                    while (p < A.size() - 1 && A[p + 1] == A[p]) p++;
                    while (q > index + 1 && A[q - 1] == A[q]) q--;
                    p++;
                    q--;
                }
            }
        }
        return targetTuple;
    }
};

